8.1 类关联结构
class Car{
    private String name;
    private double price;
    private Person person ;

    public Car(String name, double price) {
        this.name = name;
        this.price = price;
    }
    public String getInfor(){
        return "车牌："+name+" 价格"+price;
    }
    // setter getter
    public Person getPerson() {
        return person;
    }
    public void setPerson(Person person) {
        this.person = person;
    }
    public String getName() {
        return name;
    }
    public void setName(String name) {
        this.name = name;
    }
    public double getPrice() {
        return price;
    }
    public void setPrice(double price) {
        this.price = price;
    }
}

class Person{
    private String name;
    private int age;
    private Car car;
    public Person(String name, int age) {
        super();
        this.name = name;
        this.age = age;
    }
    public String getInfor() {
        return "车主："+name+" 年龄: "+age;
    }
    // setter getter    
    public String getName() {
        return name;
    }
    public Car getCar() {
        return car;
    }
    public void setCar(Car car) {
        this.car = car;
    }
    public void setName(String name) {
        this.name = name;
    }
    public int getAge() {
        return age;
    }
    public void setAge(int age) {
        this.age = age;
    }
}


public class Test {
    public static void main(String[] args) {
        Person person = new Person("张三", 20);
        Car car = new Car("劳斯莱斯", 80000000);
        // 创建关联关系
        person.setCar(car); // 一个人有一辆车
        car.setPerson(person); // 一辆车属于一个人
        // 根据关系获取数据
        System.out.println(person.getCar().getInfor()); // 车的信息
        System.out.println(car.getPerson().getInfor()); // 车主的信息
    }
}

